-- player
create table player (
  id integer not null
  , name character varying(255)
  , back_number character varying(255)
  , position character varying(255)
  , left_and_right character varying(255)
  , face_picture character varying(65535)
  , constraint player_PKC primary key (id)
) ;

comment on table player is '選手';

comment on column player.id is '選手ID';
comment on column player.name is '氏名';
comment on column player.back_number is '背番号';
comment on column player.position is 'ポジション';
comment on column player.left_and_right is '投/打';
comment on column player.face_picture is '顔写真';

-- player_comment
create table player_comment (
  id serial not null
  , player_id integer not null
  , context character varying(255)
  , my_context character varying(255)
  , create_at timestamp(6) without time zone
  , constraint player_comment_PKC primary key (id)
) ;

